  `type_id` int(11) DEFAULT NULL,
  PRIMARY KEY (`product_Id`),
  KEY `FKED8DCCEFA3A45BFC` (`type_id`),
  CONSTRAINT `FKED8DCCEFA3A45BFC` FOREIGN KEY (`type_id`) REFERENCES `ptype` (`type_id`)
) ENGINE=InnoDB AUTO_INCREMENT=161 DEFAULT CHARSET=utf8;

